import { Component, Input } from '@angular/core';
import { IChore } from './models/chores-list.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'to-do';

  @Input() createdChore: IChore;

  addChore(newChore: IChore) {
    this.createdChore = newChore;
  }
}
