import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { OnInit } from '@angular/core';
import { Observable, first, take } from 'rxjs';

@Component({
  selector: 'app-create-chore',
  templateUrl: './create-chore.component.html',
  styleUrls: ['./create-chore.component.css']
})
export class CreateChoreComponent implements OnInit {

  const idApi = `18fdca17ed63404fbf6df235aba0b429`;
   apiUrl =`https://crudcrud.com/api/${idApi}`;

  choresForm: FormGroup = new FormGroup({
    id: new FormControl(''),
    title: new FormControl('', [Validators.required]),
    category: new FormControl('', [Validators.required]),
    time: new FormControl(this.getTimeNow(), [Validators.required]),
  });

  createChore(): void {
    const title = this.choresForm.getRawValue();
    const category = this.choresForm.getRawValue();
    const time = this.choresForm.getRawValue();
    console.log(title);
    console.log(category);
    console.log(category);
  }

  getTimeNow(): string {
    const time = new Date();
    const hour = time.getHours();
    const minutes = time.getMinutes();
    const convertedTime = `${hour}:${minutes}`;
    return convertedTime;
  }

  ngOnInit(): void {
    this.titleFieldTest();
  }

  private titleFieldTest(): void {
    console.log(this.choresForm.controls['title'].valueChanges.subscribe({
      next: (res) => {
        console.log(res)
      }
    }));
  }

}
<div class="container">
  <div class="card mt-5 mx-auto w-50 p-3">
    <h5 class="card-header d-flex justify-content-center fs-1 mb-3">Gerencie seu dia</h5>
    <div class="card-body">

      <form [formGroup]="choresForm" (ngSubmit)="createChore()">
        <div class="mb-3">
          <label for="chore-title" class="form-label">Título</label>
          <input type="text" class="form-control" id="chore-title" placeholder="Digite o título da tarefa" formControlName="title">
        </div>
        <div class="mb-3">
          <label for="chore-category" class="form-label">Categoria</label>
          <input type="text" class="form-control" id="chore-category" placeholder="Digite a categoria da tarefa" formControlName="category">
        </div>
        <div class="mb-3">
          <label for="chore-time" class="form-label">Hora</label>
          <input type="time" class="form-control" id="chore-time" formControlName="time">
        </div>
        <button class="btn btn-success">Adicionar tarefa</button>
      </form>

    </div>
  </div>
</div>
