import { Component, Input } from '@angular/core';
import { IChore } from './models/chores-list.model';
import { BehaviorSubject } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  private choresSubject = new BehaviorSubject<IChore[]>([]);
  chores$ = this.choresSubject.asObservable();

  apiKey = `78adf76c3d8542de941b5538c527a637`;
  apiUrl = `https://crudcrud.com/api/${this.apiKey}/chores`;

  constructor(private http: HttpClient) {}

  private getChores(): void {
    this.http.get<IChore[]>(this.apiUrl)
      .pipe(first())
      .subscribe({
        next: (res) => {
          this.chores = res;
          console.log(res);
        },
        error: (err) => { console.log(err); },
        complete: () => { }
      })
  }

  @Input() createdChore!: IChore;

  addChore(newChore: IChore) {
    this.createdChore = newChore;
  }
}
