import { Component, OnDestroy, OnInit } from '@angular/core';

@Component({
  selector: 'app-clock',
  templateUrl: './clock.component.html',
  styleUrls: ['./clock.component.css']
})
export class ClockComponent implements OnInit, OnDestroy {
  currentDay: string | undefined;
  currentDate: string | undefined;
  currentMonth: string | undefined;
  currentHour: string | undefined;
  currentMinute: string | undefined;

  private intervalId: any;

  ngOnInit() {
    this.updateTime();
    this.intervalId = setInterval(() => {
      this.updateTime();
    }, 60000);
  }

  ngOnDestroy() {
    clearInterval(this.intervalId);
  }

  updateTime() {
    const now = new Date();
    this.currentDay = this.getDayOfWeek();
    this.currentDate = now.getDate().toLocaleString();
    this.currentMonth = (now.getMonth() + 1).toString();
    this.currentHour = now.getHours().toLocaleString();
    this.currentMinute = now.getMinutes().toLocaleString();
  }

  private getDayOfWeek(): string {
    const now = new Date();
    const daysOfWeek = [
      'Domingo',
      'Segunda-feira',
      'Terça-feira',
      'Quarta-feira',
      'Quinta-feira',
      'Sexta-feira',
      'Sábado'
    ];
    return daysOfWeek[now.getDay()];
  }
}
