import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { first } from 'rxjs';
import { IChore } from 'src/app/models/chores-list.model';

@Component({
  selector: 'app-list-chores',
  templateUrl: './list-chores.component.html',
  styleUrls: ['./list-chores.component.css']
})
export class ListChoresComponent implements OnInit {

  apiKey = `c2e48408f09849a09cf8ef168fb23ba8`;
  apiUrl = `https://crudcrud.com/api/${this.apiKey}/chores`;

  constructor(private http: HttpClient) { }

  chores?: IChore[];

  ngOnInit(): void {
    this.getChores();
  }

  private getChores(): void {
    this.http.get<IChore[]>(this.apiUrl)
      .pipe(first())
      .subscribe({
        next: (res) => {
          this.chores = res;
        },
        error: (err) => { console.log(err); },
        complete: () => { }
      })
  }

  deleteChore(id: string): void {
    const url = `${this.apiUrl}/${id}`;
    this.http.delete(url)
      .pipe(first())
      .subscribe({
        complete: () => {
          this.getChores();
        },
        error: (err) => {
          console.log(err);
        }
      })
    alert("Tarefa deletada com sucesso!");
  }

  editionForm: FormGroup = new FormGroup({
    title: new FormControl(''),
    category: new FormControl(''),
    time: new FormControl('')
  });

  actualId: string = '';

  getDataToEditForm(id: string) {
    this.http.get<IChore>(`${this.apiUrl}/${id}`)
      .pipe(first())
      .subscribe({
        next: (res) => {
          const chore = {
            _id: id,
            title: res.title,
            category: res.category,
            time: res.time
          };
          this.editionForm.patchValue(chore);
          this.actualId = id;
        },
        error: (err) => {
          console.log(err);
        }
      })
  }

  updateChore(): void {
    const chore: IChore = this.editionForm.getRawValue();
    this.http.put(`${this.apiUrl}/${this.actualId}`, chore)
      .pipe(first())
      .subscribe({
        next: (response) => {
          alert("Tarefa atualizada com sucesso!");
          this.getChores();
        },
        error: (err) => {
          console.log(err);
        }
      });

  }

}
<div class="container py-5 h-100">
  <div class="row d-flex justify-content-center align-items-center h-100">
    <div class="col col-lg-9 col-xl-7">
      <div class="card rounded-3">
        <div class="card-body p-4">

          <h4 class="text-center my-3 pb-3">O que temos para hoje</h4>

          <table class="table mb-4">
            <thead>
              <tr>
                <th scope="col">Hora</th>
                <th scope="col">Título</th>
                <th scope="col">Categoria</th>
                <th scope="col">Ações</th>
              </tr>
            </thead>
            <tbody *ngFor="let chore of chores">
              <tr>
                <th scope="row">{{chore.time}}</th>
                <td>{{chore.title}}</td>
                <td>{{chore.category}}</td>
                <td>
                  <button type="button" class="btn btn-warning" data-bs-toggle="modal" data-bs-target="#exampleModal"
                    (click)="getDataToEditForm(chore._id)">Editar</button>
                  <button data-mdb-button-init data-mdb-ripple-init class="btn btn-danger ms-1"
                    (click)="deleteChore(chore._id)">Deletar</button>
                </td>
                <!-- Modal -->
                <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                  <div class="modal-dialog">
                    <div class="modal-content">
                      <div class="modal-header">
                        <h1 class="modal-title fs-5" id="exampleModalLabel">Altere abaixo sua tarefa:</h1>
                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                      </div>
                      <div class="modal-body">
                        <form [formGroup]="editionForm" (ngSubmit)="updateChore()">
                          <div class="mb-3">
                            <label for="chore-title" class="form-label">Título</label>
                            <input type="text" class="form-control" id="chore-title" placeholder="Digite o título da tarefa"
                              formControlName="title">
                          </div>
                          <div class="mb-3">
                            <label for="chore-category" class="form-label">Categoria</label>
                            <input type="text" class="form-control" id="chore-category" placeholder="Digite a categoria da tarefa"
                              formControlName="category">
                          </div>
                          <div class="mb-3">
                            <label for="chore-time" class="form-label">Hora</label>
                            <input type="time" class="form-control" id="chore-time" formControlName="time">
                          </div>
                          <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" type="submit" class="btn btn-success" data-bs-dismiss="modal">Salvar
                              alterações</button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
                <!-- FIM DO MODAL -->
            </tbody>
          </table>

        </div>
      </div>
    </div>
  </div>
</div>
